import React, { useState } from 'react';
import './Sidebar.css';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="app-container">
      {/* Sidebar section */}
      <div className={`sidebar-container ${isOpen ? 'open' : 'closed'}`}>
        {!isOpen && (
          <button className="burger-menu" onClick={toggleSidebar}>
            ☰
          </button>
        )}
        
        <div className={`sidebar ${isOpen ? 'open' : ''}`}>
          {isOpen && (
            <>
              <button className="close-menu" onClick={toggleSidebar}>
                ✕
              </button>

              <div className="menu">
                <a href="/" className="m-1">
                  <div className="menu-item">Home</div>
                </a>
                <a href="/Courses" className="m-1">
                  <div className="menu-item">Courses</div>
                </a>
                <a href="/Learn" className="m-1">
                  <div className="menu-item">Learn Now</div>
                </a>
              </div>

              <div className="profile">
                <span>Saket Tilak</span>
              </div>
            </>
          )}
        </div>

        {!isOpen && (
          <>
            <button className="plus-button" onClick={toggleSidebar}>
              +
            </button>
            <div className="collapsed-profile">
              <img src="assets/saketprofile.png" alt="Profile" />
            </div>
          </>
        )}
      </div>

      {/* Main content section */}
      <div className="main-content">
        <h1>Welcome to CourseForge!</h1>
        <p>Here you can learn and explore various courses.</p>
        {/* Add more content here */}
      </div>
    </div>
  );
};

export default Sidebar;
