import React, { useState } from 'react';
import './Sidebar.css';
function Navbar() {


  // src/components/Sidebar.js


const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`sidebar-container ${isOpen ? 'open' : ''}`}>
      {!isOpen && (
        <button className="burger-menu" onClick={toggleSidebar}>
          ☰
        </button>
      )}
      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        {isOpen && (
          <>
            <button className="close-menu" onClick={toggleSidebar}>
              ✕
            </button>

          </>
        )}
        <div className="menu">
        <div class="float-right">
            <a href='/' className='m-1'>Home</a>
            <a href='/Courses' className='m-1'>Courses</a>
            <a href='/Learn' className='m-1'>Learn Now</a>
          </div>
        </div>
        {isOpen && (
          <div className="profile">
            <span>Saket Tilak</span>
          </div>
        )}
      </div>
      {!isOpen && (
        <>
          <button className="plus-button" onClick={toggleSidebar}>
            +
          </button>
          <div className="collapsed-profile" onClick={toggleSidebar}>
            <img src="assets/saketprofile.png" alt="Profile" />
          </div>
        </>
      )}
    </div>
  );
};
};

export default Sidebar;
