import React, { useState } from 'react';
import './Courses.css';

function App() {
  // State to track the course progress
  const [progress, setProgress] = useState(25); // Initial progress set to 25%

  // Handler for updating the progress on drag
  const handleDrag = (e) => {
    const progressBarContainer = e.target.closest('.progress-bar-container');
    const rect = progressBarContainer.getBoundingClientRect();
    const newProgress = Math.min(100, Math.max(0, ((e.clientX - rect.left) / rect.width) * 100));
    setProgress(newProgress);
  };

  // Handler to stop text selection while dragging
  const handleMouseMove = (e) => {
    if (e.buttons === 1) { // Left mouse button is clicked
      handleDrag(e);
    }
  };

  return (
    <div className="App" onMouseMove={handleMouseMove}>
      {/* Main Content */}
      <div className="main-content">
        {/* Title */}
        <header className="App-header">
          <h1 className="title">
            <span style={{ color: '#FF007F' }}>Your</span>{' '}
            <span style={{ color: '#007FFF' }}>Courses</span>
          </h1>
        </header>

        {/* Course Progress */}
        <div className="course-progress">
          <div className="course-header">
            <span>Computer Science</span>
            <br></br>
            <span>{Math.round(progress)}%</span>
          </div>
          <div
            className="progress-bar-container"
            onMouseDown={handleDrag}
            style={{ cursor: 'pointer' }}
          >
            <div
              className="progress-bar"
              style={{ width: `${progress}%` }}
            ></div>
          </div>
          <button className="expand-button">Expand</button>
        </div>

        {/* Placeholder Boxes */}
        <div className="placeholder-boxes">
          <div className="box"></div>
          <div className="box"></div>
          <div className="box"></div>
        </div>
      </div>
    </div>
  );
}

export default App;
