import React from 'react';
import './indexreact.css';

function App() {
  return (
    <div className="glow-container">
      <h1 className="title">Introducing</h1>
      <h2 className="glow-text">Illuminated Glow Text.</h2>
      <h3 className="subtitle">Highlight the main focus text.</h3>
      <p className="description">
        Experience a new way to draw attention to key elements with stunning illuminated text. 
        Perfect for making a bold statement, this dynamic design ensures your content stands out effortlessly.
      </p>
    </div>
  );
}

export default App;
