import React from 'react';
import './GoogleButton.css'; // Import your CSS file

const GoogleButton = ({ onClick }) => { // Accept onClick as a prop
    return (
        <button className="google-auth-button" onClick={onClick}>
            <div className="google-button">
                <span>Sign in with Google</span>
                <img
                    src="data:image/svg+xml;base64,"
                    alt="Google Logo"
                    className="google-logo"
                />
            </div>
        </button>
    );
};

export default GoogleButton;
